mapper.update:
update board_free
set viewcount = viewcount + 1
where freeboardcode = #{freeboardcode}

update board_free
set title   = #{title},
    content = #{content}
where usercode = #{usercode}

UPDATE board_free
SET viewcount = viewcount + 1
WHERE freeboardcode = #{freeboardcode}

mapper.select:
SELECT * FROM board_free
where ${searchType} like concat('%',#{searchWord},'%')
ORDER BY registereddate DESC LIMIT #{pageStart}, #{perPageNum}
