mapper.select:
select count(*) from tour where title like concat('%',#{searchword},'%')

	and contenttype in

		#{code}



	and sigungucode = #{sigungucode}

select t.*, COUNT(l.tourcode) as likes from tour as t LEFT JOIN tour_likes as l ON t.tourcode = l.tourcode where t.title LIKE CONCAT('%',#{searchword},'%')
and t.contenttype in
#{code}
and t.sigungucode = #{sigungucode}
group by t.tourcode order by t.title limit #{start}, 12

select t.*, COUNT(l.tourcode) as likes, (SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END FROM tour_likes tk WHERE tk.tourcode = t.tourcode AND tk.usercode = #{usercode}) AS likchk from tour as t LEFT JOIN tour_likes as l ON t.tourcode = l.tourcode where t.title LIKE CONCAT('%',#{searchword},'%')
and t.contenttype in
#{code}
and t.sigungucode = #{sigungucode}
group by t.tourcode order by t.title limit #{start}, 12
